    }
    ... on ProjectV2IterationField {
      configuration {
        iterations {
          title
          duration
          startDate
        }
        completedIterations {
          title
          duration
          startDate
        }
        duration
        startDay
      }
    }
  }
}

      }
    }
  }
}

